    width: "80%",
    padding: "10px",
    margin: "auto",
}

const sectionTitle = {
    fontWeight: 'bold',
    fontSize: '18px',
    marginTop: '20px',
}

const container = {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
}

const inputText = {
    width: "200px",
    margin: "10px",
    fontSize: "14px",
}
function App() {
    return (

            <div style={searchField}>
                <Form>
                    <Form.Group controlId="formBasicInput">
                        <div style={sectionTitle}>
                            <Form.Label>Please input keywords (not required): </Form.Label>
                        </div>
                        <div style={container}>
                            <Form.Control style={inputText} type="pubmedID" placeholder="PubMed ID" />
                            <Form.Control style={inputText} type="gene" placeholder="Gene Name (e.g. GAPDH)" />
                            <Form.Control style={inputText} type="pubmedID" placeholder="Author's Lastname (e.g. Webster)" />
                            <Form.Control style={inputText} type="gene" placeholder="Disease (e.g. Melanoma)" />
                        </div>

                        {/*<Form.Text className="text-muted">*/}
                        {/*    Can put any comment, instructions, etc.*/}
                        {/*</Form.Text>*/}
                    </Form.Group>

                    <Form.Group controlId="formBasicCheckbox">
                        <div style={sectionTitle}>
                            <Form.Label>Please select isolation methods (not required): </Form.Label>
                        </div>
                        <div style={container}>
                            <Form.Check style={inputText} type="checkbox" label="Ultracentrifugation" />
                            <Form.Check style={inputText} type="checkbox" label="Density Gradient" />
                            <Form.Check style={inputText} type="checkbox" label="Size Exclusion Chromatography" />
                            <Form.Check style={inputText} type="checkbox" label="Ultrafiltration" />

                        </div>

                        {/*<Form.Text className="text-muted">*/}
                        {/*    Can put any comment, instructions, etc.*/}
                        {/*</Form.Text>*/}
                    </Form.Group>


                    <Form.Group className="mb-3" controlId="formBasicCheckbox">
                        <Form.Check type="checkbox" label="Check me out" />
                    </Form.Group>
                    <Button variant="primary" type="submit">
                        Submit
                    </Button>
